import axios from 'axios';
export const getAddressInfo = async (nodeUrl, address, params) => {
  const url = `${nodeUrl}/addresses/data/${address}`;
  const response = await axios.get(url, params);
  return response;
};
export const getAddressDefaultBalance = async (params, axiosConfig) => {
  const {
    nodeUrl,
    address
  } = params;
  const url = `${nodeUrl}/addresses/balance/${address}`;
  const response = await axios.get(url, axiosConfig || {});
  return response;
};
